    {"action_type": "MouseAction", "mouse_action_type": "click", "mouse_button": "left", "mouse_position": {"width": 464, "height": 279}},
    {"action_type": "KeyboardAction", "keyboard_action_type": "press", "keyboard_key": "Control_L+a"}
